pragma solidity ^0.4.11;

contract Election{
	
	struct Candidate{
		uint id;
		string name;
		uint voteCount;
	}

	mapping(uint => Candidate) public candidates;
	uint public candidateCount; // mapping returns default if key does not exist
	//impossible to get count of candidates in mapping

	function Election() public{
		candidateCount = 0;
		addCandidate("Sarvesh");
		addCandidate("Sharad");
	}

	function addCandidate(string _name) private{
		candidateCount++;
		candidates[candidateCount] = Candidate(candidateCount,_name,0);
	}
}
